import React, { useState, useRef, useEffect } from 'react';
import './App.css';

function App() {
  const [isCameraOn, setIsCameraOn] = useState(false);
  const [capturedImage, setCapturedImage] = useState(null);
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  // コンポーネントがアンマウントされる時にカメラストリームをクリーンアップします
  useEffect(() => {
    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const startCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.onloadedmetadata = () => {
          setIsCameraOn(true);
          setCapturedImage(null); // 新しい撮影のために前の画像をクリア
        };
        videoRef.current.srcObject = stream; // onloadedmetadata の設定後に srcObject を設定
        streamRef.current = stream; // stream を ref に保持
      } else {
        setIsCameraOn(true);
      }
    } catch (err) {
      console.error("カメラへのアクセスエラー: ", err);
      alert("カメラにアクセスできませんでした。ブラウザの権限設定を確認してください。");
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }
    setIsCameraOn(false);
  };

  const captureImage = () => {
    if (videoRef.current && videoRef.current.readyState >= 2) { // readyState >= 2 (HAVE_CURRENT_DATA) で撮影可能
      const canvas = document.createElement('canvas');
      canvas.width = videoRef.current.videoWidth;
      canvas.height = videoRef.current.videoHeight;
      const context = canvas.getContext('2d');

      // 映像が左右反転しているので、撮影する画像も同様に反転させます
      context.translate(canvas.width, 0);
      context.scale(-1, 1);

      context.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
      const imageUrl = canvas.toDataURL('image/png');
      setCapturedImage(imageUrl);
    } else {
      alert("カメラの準備ができていません。少し待ってからもう一度お試しください。");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>カメラアプリ</h1>
        <p className="description">カメラで写真を撮って表示するシンプルなアプリです。</p>

        <div className="camera-container">
          {!isCameraOn && !capturedImage && (
            <button onClick={startCamera} className="camera-button">カメラを起動</button>
          )}
          {isCameraOn && !capturedImage && (
            <div className="video-container">
              <video ref={videoRef} autoPlay playsInline className="video-feed" />
              <button onClick={captureImage} className="camera-button capture-button">撮影</button>
              <button onClick={stopCamera} className="camera-button close-button">閉じる</button>
            </div>
          )}
        </div>

        {capturedImage && (
          <div className="captured-image-container">
            <h2>撮影した画像:</h2>
            <img src={capturedImage} alt="Captured" className="captured-image" />
            <button onClick={startCamera} className="camera-button">もう一度撮る</button>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
